pageContext.rootQuery:
{
  prismic {
    home_page(lang: "en-us", uid: "home") {
      hero_title
      hero_button_text
      hero_button_link {
        ... on PRISMIC__ExternalLink {
          _linkType
          url
        }
      }
      about_title
      about_bio
      about_links {
        about_link
      }
    }
    allProducts(sortBy: order_ASC, first: 1) {
      edges {
        node {
          product_title
          product_preview_description
          product_preview_thumbnail
          product_category
          _meta {
            uid
          }
        }
      }
    }
    allUpdates(sortBy: priority_DESC) {
      edges {
        node {
          update_title
          update_type
          update_body
          update_link {
            ... on PRISMIC__ExternalLink {
              _linkType
              url
            }
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      title
      description
      author
    }
  }
}
