pageContext.rootQuery:
query ProductQuery($uid: String) {
  prismic {
    allProducts(uid: $uid) {
      edges {
        node {
          product_title
          product_preview_description
          product_preview_thumbnail
          product_category
          product_hero_image
          product_description
          _meta {
            uid
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      title
      description
      author
    }
  }
}
